    function setUpCollisionBlocks() {
        for (let i = 0; i < buildings.length; i++) {
            let building = buildings[i];
            if (building.name == "wall rect") {
                let width = building.width;
                let height = building.height;
                let middleX = building.x + (building.width / 2);
                let middleY = building.y + (building.height / 2);
                let onlyDir1 = ((building.y + height >= mapInfo.y - 50) ? -(Math.PI / 2) : (building.y <= 50) ? Math.PI / 2 : null);
                let onlyDir2 = ((building.x + width >= mapInfo.x - 50) ? Math.PI : (building.x <= 50) ? 0 : null);
                if (building.x - 30 > 0) {
                    buildings.push({
                        name: "coll rect",
                        x: building.x - 30,
                        y: building.y - 50,
                        onlyDir: building.onlyDir_up_down || onlyDir1,
                        middleY: middleY,
                        height: height + 50,
                        width: 30
                    });
                }
                if (building.x + width + 30 < mapInfo.x) {
                    buildings.push({
                        name: "coll rect",
                        x: building.x + width,
                        y: building.y - 50,
                        middleY: middleY,
                        onlyDir: building.onlyDir_up_down || onlyDir1,
                        height: height + 50,
                        width: 30
                    });
                }
                if (building.y - 30 > 0) {
                    buildings.push({
                        name: "coll rect",
                        x: building.x,
                        y: building.y - 30,
                        middleX: middleX,
                        onlyDir: building.onlyDir_left_right || onlyDir2,
                        height: 30,
                        width: width
                    });
                }
                if (building.y + height + 30 < mapInfo.y) {
                    buildings.push({
                        name: "coll rect",
                        x: building.x,
                        y: building.y + height,
                        middleX: middleX,
                        onlyDir: building.onlyDir_left_right || onlyDir2,
                        height: 30,
                        width: width
                    });
                }
            }
        }
    }
