    function getLeaguePoints(rewardsMulti) {
        let points = player.league;
        let reward = 0;
        let E = rewardsMulti >= 3 ? false : true;
        if (points >= 7e3) {
            reward = E ? -100 : 15;
        } else if (points >= 6e3) {
            reward = E ? -75 : 45;
        } else if (points >= 5e3) {
            reward = E ? -50 : 75;
        } else if (points >= 4e3) {
            reward = E ? -35 : 105;
        } else if (points >= 3e3) {
            reward = E ? -25 : 150;
        } else if (points >= 2e3) {
            reward = E ? -15 : 225;
        } else if (points >= 1e3) {
            reward = E ? -10 : 255;
        } else if (points < 1e3) {
            reward = E ? -5 : 300;
        }
        player.league += reward;
        if (player.league < 0) {
            player.league = 0;
        }
    }
