    function doLeagueThingyMath(league) {
        if (league >= 4701) {
            return 1;
        }

        const ranges = [
            { min: 3801, max: 4700 },
            { min: 2901, max: 3800 },
            { min: 2101, max: 2900 },
            { min: 1501, max: 2100 },
            { min: 1001, max: 1500 },
            { min: 701, max: 1000 },
            { min: 451, max: 700 }
        ];

        for (const range of ranges) {
            if (league >= range.min && league <= range.max) {
                const min = league - range.min;
                const max = range.max - range.min;
                return min / max;
            }
        }

        return league / 450;
    }
