                let middleX = building.x + (building.width / 2);
                let middleY = building.y + (building.height / 2);
                let onlyDir1 = ((building.y + height >= mapInfo.y - 50) ? -(Math.PI / 2) : (building.y <= 50) ? Math.PI / 2 : null);
